import React, { useState, useRef } from "react";
import "./styles.css";
import { FaArrowUp } from "react-icons/fa";
function App() {
  const [inputText, setInputText] = useState("");
  const [messages, setNewMessage] = useState(["How can I help you?"]);


  const addMessage = (e) => {
    e.preventDefault();
    setNewMessage([...messages, inputText]);
    console.log(messages);
  };
  var input = document.getElementById("myInput");
input.addEventListener("keyup", function(event) {
  if (event.keyCode === 13) {
   event.preventDefault();
   document.getElementById("myBtn").click();
  }
});
  return (
    <div className="container">
      <div className="chat-container">
        <div className="chat">
          <MessageList messages={messages} />
        </div>
      </div>
      <div id="myInput" className="chat-input-container">
        <input
          className="chat-input"
          onChange={(e) => {
            setInputText(e.target.value);
          }}
          placeholder="Enter Text"
        />
        <div className="add-question-button" onClick={addMessage}>
          <FaArrowUp />
        </div>
      </div>
    </div>
  );
}

function MessageList(props) {
  const messages = props.messages;
  const listMessages = messages.map((message) => (
    <Message key={message} value={message} />
  ));

  return <div>{listMessages}</div>;
}

function Message(props) {
  return <p>{props.value}</p>;
}

export default App;
