import React,{useState, useEffect} from 'react';
import "./styles.css"

function App() {

  const questions = ['Lorem ipsum dolor sit amet?', 'Lorem ipsum dolor sit amet2?', 'Lorem ipsum dolor sit amet3?']

  const [messages, setMessages] = useState(['How can I help you?']);

  const handleAddMessage = () => {

  };

  const [records, setRecords] = useState([]);

 // This method fetches the records from the database.
 useEffect(() => {
   async function getRecords() {
     const response = await fetch(`http://localhost:5000/record/`);

     if (!response.ok) {
       const message = `An error occurred: ${response.statusText}`;
       window.alert(message);
       return;
     }

     const records = await response.json();
     setRecords(records);
   }

   getRecords();

   return;
 }, [records.length]);

 // This method will map out the records on the table
 function recordList() {
  return records.map((record) => {
    return (
      <Record
        record={record}
        deleteRecord={() => deleteRecord(record._id)}
        key={record._id}
      />
    );
  });
}
  return (

        <div className='container'>
          <div className='chat-container'>
            <div className='chat'>
                <MessageList messages={messages}/>
            </div>
          </div>
          <div className='chat-input-container'>
            <div className='chat-input'>
              <QuestionList questions={questions}/>
            </div>
          </div>
        </div>

  );
}

function MessageList(props) {
  const messages = props.messages;
  const listMessages = messages.map((message) => 
    <Message key={message} value={message}/>
  );

  return (
    <div>
      {listMessages}
    </div>
  )
}

function Message(props) {
  return <p>{props.value}</p>
}

function Question(props) {
  return <button>{props.value}</button>
}

function QuestionList(props) {
  const questions = props.questions;
  const listQuestions = questions.map((question) => 
    <Question key={question} value={question}/>
  );

  return (
    <div>
      {listQuestions}
    </div>
  )
}
export default App;
