                  <div id="userInput">
                    <input id="textInput" class="input-box"  type="text" name="msg" placeholder="Tap to Enter to send a message">

                    <p></p>

                    <div class="chat-bar-icons">

                    </div>
                    </input>
                  </div>

                 </div>
                </div>
              </div>
            </div>
          </div>
      </div>
    </div>
  );
